CommonData.BannerCustomCSS:
/** 
 * Marketing Web Styles
 */

 @font-face {
	font-family: "Inter";
	font-display: swap;
	font-weight: 400;
	src: local("Inter Regular"), local("Inter-Regular"),
		url("https://components.unrealengine.com/fonts/inter/Inter-Regular.woff2") format("woff2");
}

@font-face {
	font-family: "Inter";
	font-display: swap;
	font-weight: 500;
	src: local("Inter Medium"), local("Inter-Medium"),
		url("https://components.unrealengine.com/fonts/inter/Inter-Medium.woff2") format("woff2");
}

@font-face {
	font-family: "Inter Tight";
	font-display: swap;
	font-weight: 700;
	src: local("Inter Tight Bold"), local("InterTight-Bold"),
		url("https://components.unrealengine.com/fonts/inter/InterTight-Bold.woff2") format("woff2");
}

@font-face {
	font-family: "Inter Tight";
	font-display: swap;
	font-weight: 900;
	src: local("Inter Tight Black"), local("InterTight-Black"),
		url("https://components.unrealengine.com/fonts/inter/InterTight-Black.woff2") format("woff2");
}

#onetrust-banner-sdk {
    -webkit-font-smoothing: antialiased !important;
}

#onetrust-banner-sdk:focus-visible {
    outline: none;
}

#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-policy {
    margin: 0;
    display: flex;
    flex-direction: column;
    gap: 10px;
}
#onetrust-consent-sdk .ot-bnr-logo {
    display: none;
}

#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-group-container::before {
    content: '';
    display: flex;
    margin: 0;
    opacity: 1;
    height: 64px;
    min-width: 64px;
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
    align-content: center;
    justify-content: center;
}


#onetrust-consent-sdk #onetrust-banner-sdk.otFloatingRounded {
    border-radius: 24px;
    border: 1px solid var(--transparent-light-010, rgba(255, 255, 255, 0.10));
    background: var(--material-elevated-high-strong, rgba(48, 48, 52, 0.70));
    padding: 24px;
    box-sizing: border-box;
    left: 50%;
    transform: translateX(-50%);
    backdrop-filter: blur(calc(var(--material-blur, 100px) / 2));

}

#onetrust-consent-sdk #onetrust-banner-sdk.otFloatingRounded {
    background-color: rgba(48, 48, 52, 0.70);
}

#onetrust-consent-sdk #onetrust-banner-sdk .ot-sdk-row {
    display: flex;
    gap: 16px;
    flex-direction: column;
    margin-inline-end: 72px;
}
#onetrust-consent-sdk #onetrust-banner-sdk .ot-sdk-row:after {
    display: none;
}

#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group-parent {
    display: flex;
    padding: 0;
    width: auto;
    min-width: fit-content;
    margin-inline-start: 86px;
}

#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group {
    display: flex;
    flex-direction: row;
    /** flex-direction: row-reverse is needed in preview :shrug: */ 
    width: auto;
    margin: 0;
    min-width: auto;
    float: unset;
    justify-content: flex-end;
    gap: 24px;
    flex-wrap: wrap;
}

#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group button {
    min-width: unset;
    margin: 0;
}

#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group .onetrust-banner-options {
    float: unset;
    display: flex;
    flex-direction: row-reverse;
    min-width: initial;
    gap: 12px;
    max-width: initial;
}

#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group .onetrust-banner-options button {
    border-radius: var(--borderRadius-6, 6px);
    display: flex;
    padding: 4px 12px;
    justify-content: center;
    align-items: center;
    align-self: stretch;
    color: var(--text-onAccent, #000);
    text-align: center;
    /* UI Text/SM Medium */
    font-family: var(--family-base, Inter);
    font-size: var(--font-14, 14px);
    font-style: normal;
    font-weight: var(--weight-medium, 500);
    line-height: 130%; /* 15.6px */
    letter-spacing: 0.36px;
    border: none;
    transition: background 0.2s cubic-bezier(0,0,1,1);
}
#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group .onetrust-banner-options button:hover {
    background: rgb(114, 211, 255);
}
#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group .onetrust-banner-options button:nth-child(1) {
    color: var(--text-primary, #FFF);
    background: var(--fill-secondary-default, rgba(255, 255, 255, 0.15));
}
#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group .onetrust-banner-options button:nth-child(1):hover {
    background: rgba(255, 255, 255, 0.35);
}



#onetrust-consent-sdk #onetrust-group-container {
    display: flex;
    float: initial;
    gap: 24px;
    align-items: flex-start;
}

#onetrust-consent-sdk #onetrust-group-container .ot-bnr-logo {
    position: static;
    min-width: 64px;
}

#onetrust-consent-sdk #onetrust-banner-sdk.ot-bnr-w-logo #onetrust-policy {
    margin: 0;
    display: flex;
    flex-direction: column;
    gap: 10px;
}

#onetrust-consent-sdk #onetrust-banner-sdk #onetrust-policy-title {
    padding: 0;
    font-family: var(--family-display, "Inter Tight");
    font-size: var(--font-20, 20px);
    font-style: normal;
    font-weight: var(--weight-bold, 700);
    line-height: 100%; /* 20px */
    letter-spacing: 0.4px;
}

#onetrust-consent-sdk #onetrust-banner-sdk .ot-sdk-container #onetrust-policy-text {
    color: var(--text-primary, #FFF);
    font-family: var(--family-base, Inter);
    font-size: var(--font-14, 14px);
    font-style: normal;
    font-weight: var(--weight-regular, 400);
    line-height: 140%; /* 19.6px */
    letter-spacing: 0.28px;
    padding: 0;
}

#onetrust-consent-sdk #onetrust-banner-sdk .ot-sdk-container #onetrust-policy-text a {
    font-weight: normal;
    transition: color 0.2s cubic-bezier(0,0,1,1);
}
#onetrust-consent-sdk #onetrust-banner-sdk .ot-sdk-container #onetrust-policy-text a:hover {
    color: rgb(114, 211, 255);
    text-decoration: underline;
}


#onetrust-consent-sdk #onetrust-button-group {
    flex-direction: column;
    gap: 8px;
    display: flex;
}
#onetrust-banner-sdk .has-reject-all-button #onetrust-pc-btn-handler.cookie-setting-link {
    text-align: center;
    transition: color 0.2s cubic-bezier(0,0,1,1);
    text-decoration: underline;
}
#onetrust-banner-sdk .has-reject-all-button #onetrust-pc-btn-handler.cookie-setting-link:hover {
    color: rgb(114, 211, 255);
    text-decoration: underline;
}
#onetrust-consent-sdk #onetrust-pc-btn-handler, #onetrust-consent-sdk #onetrust-pc-btn-handler.cookie-setting-link {
    color: var(--text-primary, #26bbff);
    font-family: var(--family-base, Inter);
    font-size: var(--font-12, 12px);
    font-style: normal;
    font-weight: var(--weight-regular, 400);
    line-height: 140%;
    letter-spacing: 0.28px;
    opacity: 1;
    background-color: transparent;
    outline-offset: unset;
    padding: 0;
    text-align: center;
}

#onetrust-consent-sdk #onetrust-close-btn-container {
    border-radius: var(--borderRadius-full, 9999px);
    background: var(--fill-secondary-default, rgba(255, 255, 255, 0.15));
    transition: background 0.2s cubic-bezier(0,0,1,1);
    height: 24px;
    width: 24px;
    min-width: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 1;
    margin: 0;
    float: unset;
    position: absolute;
    right: -4px;
    top: -6px;
}

#onetrust-consent-sdk #onetrust-close-btn-container button.ot-close-icon {
    color: white;
    margin: 0;
    opacity: 1;
    height: 24px;
    width: 24px;
    background-image: url(data:image/svg+xml;base64,) !important; /* Override OT*/
    background-size: unset;
}

#onetrust-consent-sdk #onetrust-close-btn-container:hover {
    background: rgba(255, 255, 255, 0.35);
}


@media (max-width: 768px) {
    #onetrust-consent-sdk #onetrust-banner-sdk #onetrust-policy {
        flex-direction: column;
        align-items: center;
    }
    #onetrust-consent-sdk #onetrust-banner-sdk #onetrust-group-container::before {
        margin: 0 auto;
    }
    #onetrust-consent-sdk #onetrust-banner-sdk .ot-sdk-row {
        flex-direction: column;
        margin-inline-end: 0;
    }
    #onetrust-consent-sdk #onetrust-banner-sdk #onetrust-close-btn-container {
        position: absolute;
        right: -6px;
        top: -6px;
    }
    #onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group{
        gap: 12px;
    }
    #onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group .onetrust-banner-options {
        flex-direction: row-reverse;
        justify-content: center;
    }
    #onetrust-banner-sdk .has-reject-all-button #onetrust-pc-btn-handler {
        min-width: 100%;
    }
    #onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group .onetrust-banner-options button {
        /* UI Text/MD Medium */
        font-family: var(--family-base, Inter);
        font-size: var(--font-14, 14px);
        font-style: normal;
        font-weight: var(--weight-medium, 500);
        line-height: 140%; /* 19.6px */
        letter-spacing: 0.28px;
        padding: 8px 16px;
    }
    #onetrust-consent-sdk #onetrust-group-container {
        flex-direction: column;
    }
    #onetrust-consent-sdk #onetrust-banner-sdk .ot-sdk-container #onetrust-policy-text {
        text-align: center;
    }
    #onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group-parent, #onetrust-consent-sdk #onetrust-banner-sdk #onetrust-button-group {
        width: 100%;
        margin-inline-start: 0;
        justify-content: center;
    }

}

